import React from 'react';

const timeSlots = [
  '08:00 - 09:35',
  '09:50 - 11:25',
  '11:40 - 13:15',
  '14:00 - 15:35',
  '15:50 - 17:25',
  '17:40 - 19:15',
];

const evenWeekSchedule = [
  { day: 'Понедельник', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Вторник', subjects: timeSlots.map(time => ({ time, subject: 'Блокчейн', room: '519' })) },
  { day: 'Среда', subjects: timeSlots.map(time => ({ time, subject: '', room: '519' })) },
  { day: 'Четверг', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Пятница', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
];

const oddWeekSchedule = [
  { day: 'Понедельник', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Вторник', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Среда', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Четверг', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Пятница', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
];

const Schedule = ({ isEvenWeek }) => {
  const schedule = isEvenWeek ? evenWeekSchedule : oddWeekSchedule;

  return (
    <div className="schedule-container">
      <table className="schedule-table">
        <thead>
          <tr>
            <th>День недели</th>
            <th>Время</th>
            <th>Предмет</th>
            <th>Кабинет</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((item, index) => (
            <React.Fragment key={index}>
              {item.subjects.map((subject, subIndex) => (
                <tr key={subIndex}>
                  {subIndex === 0 && <td rowSpan={item.subjects.length}>{item.day}</td>}
                  <td>{subject.time}</td>
                  <td>{subject.subject}</td>
                  <td>{subject.room}</td>
                </tr>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Schedule;
