import React from 'react';

const evenWeekSchedule = [
  // Расписание для четной недели
  { day: 'Понедельник', subject: 'Математика' },
  { day: 'Вторник', subject: 'Физика' },
  // добавь другие дни
];

const oddWeekSchedule = [
  // Расписание для нечетной недели
  { day: 'Понедельник', subject: 'Литература' },
  { day: 'Вторник', subject: 'Химия' },
  // добавь другие дни
];

const Schedule = ({ isEvenWeek }) => {
  const schedule = isEvenWeek ? evenWeekSchedule : oddWeekSchedule;

  return (
    <div>
      <h1>{isEvenWeek ? 'Четная неделя' : 'Нечетная неделя'}</h1>
      <ul>
        {schedule.map((item, index) => (
          <li key={index}>{item.day}: {item.subject}</li>
        ))}
      </ul>
    </div>
  );
};

export default Schedule;
