import React from 'react';

const evenWeekSchedule = [
  { day: 'Понедельник', subjects: [{ name: 'Математика', time: '9:00-10:30', room: '101' }, { name: 'Физика', time: '10:45-12:15', room: '102' }] },
  { day: 'Вторник', subjects: [{ name: 'Информатика', time: '9:00-10:30', room: '103' }, { name: 'Английский язык', time: '10:45-12:15', room: '104' }] },
  { day: 'Среда', subjects: [{ name: 'География', time: '9:00-10:30', room: '105' }, { name: 'Физкультура', time: '11:00-12:30', room: 'Спортзал' }] },
  { day: 'Четверг', subjects: [{ name: 'Биология', time: '9:00-10:30', room: '106' }, { name: 'История', time: '10:45-12:15', room: '107' }] },
  { day: 'Пятница', subjects: [{ name: 'Химия', time: '9:00-10:30', room: '108' }, { name: 'Математика', time: '10:45-12:15', room: '101' }] },
];

const oddWeekSchedule = [
  { day: 'Понедельник', subjects: [{ name: 'Литература', time: '9:00-10:30', room: '109' }, { name: 'История', time: '10:45-12:15', room: '107' }] },
  { day: 'Вторник', subjects: [{ name: 'Физика', time: '9:00-10:30', room: '102' }, { name: 'Химия', time: '10:45-12:15', room: '108' }] },
  { day: 'Среда', subjects: [{ name: 'Английский язык', time: '9:00-10:30', room: '104' }, { name: 'Физкультура', time: '11:00-12:30', room: 'Спортзал' }] },
  { day: 'Четверг', subjects: [{ name: 'Математика', time: '9:00-10:30', room: '101' }, { name: 'Информатика', time: '10:45-12:15', room: '103' }] },
  { day: 'Пятница', subjects: [{ name: 'Биология', time: '9:00-10:30', room: '106' }, { name: 'География', time: '10:45-12:15', room: '105' }] },
];

const Schedule = ({ isEvenWeek }) => {
  const schedule = isEvenWeek ? evenWeekSchedule : oddWeekSchedule;

  return (
    <div className="schedule-container">
      <table className="schedule-table">
        <thead>
          <tr>
            <th>День недели</th>
            <th>Предмет</th>
            <th>Время</th>
            <th>Кабинет</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((item, index) => (
            <React.Fragment key={index}>
              {item.subjects.map((subject, subIndex) => (
                <tr key={subIndex}>
                  {subIndex === 0 && <td rowSpan={item.subjects.length}>{item.day}</td>}
                  <td>{subject.name}</td>
                  <td>{subject.time}</td>
                  <td>{subject.room}</td>
                </tr>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Schedule;
