import React from 'react';

const timeSlots = [
  '08:00 - 09:35',
  '09:50 - 11:25',
  '11:40 - 13:15',
  '14:00 - 15:35',
  '15:50 - 17:25',
  '17:40 - 19:15',
];

const evenWeekSchedule = [
  { day: 'Понедельник', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Вторник', subjects: [
    { time: '09:50 - 11:25', subject: 'Блокчейн', room: '519' },
    { time: '11:40 - 13:15', subject: 'Мат моделирование', room: '519' },
    { time: '14:00 - 15:35', subject: 'Мат моделирование*', room: '324' },
    { time: '17:40 - 19:15', subject: 'Основы безопастности IT', room: '' },
  ] },
  { day: 'Среда', subjects: [
    { time: '11:40 - 13:15', subject: 'Анализ данных', room: '519' },
    { time: '14:00 - 15:35', subject: 'Анализ данных*', room: '445' },
  ] },
  { day: 'Четверг', subjects: [
    { time: '08:00 - 09:35', subject: 'Методы тестирования*', room: '324' },
    { time: '09:50 - 11:25', subject: 'Методы тестирования', room: '424' },
    { time: '14:00 - 15:35', subject: 'Разработка мобильных приложений', room: '' },
  ] },
  { day: 'Пятница', subjects: [
    { time: '14:00 - 15:35', subject: 'Системный анализ*', room: '449' },
    { time: '15:50 - 17:25', subject: 'Системный анализ', room: '436' },
  ] },
  { day: 'Суббота', subjects: [
    { time: '09:50 - 11:25', subject: 'Основы безопастности IT', room: '533' },
    { time: '11:40 - 13:15', subject: 'Проектная деятельность', room: '533' },
  ] },
];

const oddWeekSchedule = [
  { day: 'Понедельник', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Вторник', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Среда', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Четверг', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Пятница', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
  { day: 'Суббота', subjects: timeSlots.map(time => ({ time, subject: '', room: '' })) },
];

const Schedule = ({ isEvenWeek }) => {
  const schedule = isEvenWeek ? evenWeekSchedule : oddWeekSchedule;

  return (
    <div className="schedule-container">
      <table className="schedule-table">
        <thead>
          <tr>
            <th>День недели</th>
            <th>Время</th>
            <th>Предмет</th>
            <th>Кабинет</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((item, index) => (
            <React.Fragment key={index}>
              {item.subjects.map((subject, subIndex) => (
                <tr key={subIndex}>
                  {subIndex === 0 && <td rowSpan={item.subjects.length}>{item.day}</td>}
                  <td>{subject.time}</td>
                  <td>{subject.subject || '-'}</td> {/* Показать "-" если предмет пустой */}
                  <td>{subject.room || '-'}</td>    {/* Показать "-" если кабинет пустой */}
                </tr>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Schedule;
